import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import './assets/style.css';
import QuizStart from './components/QuizStart';

class Quiz extends Component {
  state={
    playing: 0,
    numberq: 0,
    score: 0
  };

  onNumberqChange = (newNumberq) =>
    newNumberq<=10 ? this.setState({numberq: newNumberq}) : this.setState({numberq: 10});

onStart = () => this.setState({playing: 1});

  render() {
    return (
      <div className="container">
        <div className="title">Quiz</div>
        {this.state.playing===0&&
        <QuizStart
          numberq={this.numberq}
          onNumberqChange={this.onNumberqChange}
          onStart={this.onStart}
        />
        }

      </div>
    );
  }
}


ReactDOM.render(
  <Quiz />,
  document.getElementById('root')
);
