import React, {Component} from 'react';
import Kuroshiro from 'kuroshiro';
import KuromojiAnalyzer from "kuroshiro-analyzer-kuromoji";
import { Tooltip } from 'reactstrap';
import ReactTooltip from "react-tooltip";


class Subtitle extends Component {
  constructor(props) {
    super(props)
    this.myRef = React.createRef()

  }

  state={
    tooltipOpen: false,
  }

  toggle = () => this.setState({tooltipOpen: window.getSelection().toString() !== ''&&!this.state.tooltipOpen});

  toFuri = async (jp) => {
    var kuroshiro = new Kuroshiro();
    await kuroshiro.init(new KuromojiAnalyzer({ dictPath: "/dict" }));
    return await kuroshiro.convert(jp, { to: "hiragana" });
  }

  onHighlight = () => {
    console.log("Entered onHighlight");
    console.log(window.getSelection().getRangeAt(0).getBoundingClientRect())
    console.log(this.divRef)

    if (window.getSelection().toString() !== '') {
      this.toFuri(window.getSelection().toString()).then(
        (result) => console.log(result)
      )
    }
  }

  render() {
    return (
      <div
        className="subtitle"
        onClick={this.onHighlight}>

        <p><span href="#" id="jpSub">{this.props.jpSub}</span></p>
        <Tooltip placement='bottom' offset={-200} isOpen={this.state.tooltipOpen} autohide={false} target="jpSub" toggle={this.toggle}>
          {window.getSelection().toString()}
        </Tooltip>

        <br/>

        {this.props.engSub}
      </div>
    );
  }
}

export default Subtitle;
