import React, {Component} from 'react';
import ResponsivePlayer from './ResponsivePlayer';
import Subtitle from './Subtitle';
import './Video.css';
import sub_one from '../assets/subtitle_json/Attack.on.Titan.01.To.You.in.2000.Years.720p.Bluray.x264.Kirion.json';


class Video extends Component {
  state={
    url: 'https://www.youtube.com/watch?v=x7hjx8OPiA0', //Placeholder
    played: 0, //In seconds
    counter: 0,
    subtitle_text: ''
  }

  handleProgress = (state) => {
    this.setState({played: state.playedSeconds});
    // console.log(this.state.played);
  }

  playedToSub = (played) => {
    while (this.state.counter<sub_one.length) {
      console.log("Entered while, counter: ", this.state.counter)
      if (sub_one[this.state.counter].start < played
        && played < sub_one[this.state.counter].end) {
        console.log("Entered if, counter: ", this.state.counter)
        return sub_one[this.state.counter].content
      }
      else {
        console.log("Entered else, counter: ", this.state.counter)
        this.setState({counter: this.state.counter+1})
      }
    }
  }

  render() {
    return(
      <div className="container">
        <div className="video-title">Video Learning</div>
        <div className="video-board">
          <ResponsivePlayer
            url={this.state.url}
            handleProgress={this.handleProgress}
          />
          <Subtitle
            played={this.state.played}
          />
        </div>
      </div>
    );
  }
}

export default Video;
