import React, {Component} from 'react';

class QuizState extends Component {
  render() {
    return (
      <div className="score-board">
        <div className="quiz-inner">

          Question {this.props.numberq-this.props.qBank.length+1}
          Type the Hiragana reading!
          <br /><br />

          {this.props.qBank[0].kanji}
          <br /><br />

          <input className="quizAnswer" type="text" onChange={this.props.onAnswerChange} />
          <br /><br />

          <button className="submitBtn" onClick={this.props.onSubmit}>Submit</button>
          <button className="skipBtn" onClick={this.props.onSkip}>Skip</button>
        </div>
      </div>
    );
  }

}

export default QuizState;
