import React from 'react';
import Kuroshiro from 'kuroshiro';
import KuromojiAnalyzer from "kuroshiro-analyzer-kuromoji";


const toFuri = (jp) => {
  var kuroshiro = new Kuroshiro();

  kuroshiro.init(new KuromojiAnalyzer({ dictPath: "/dict" }))
    .then(() => {
      return kuroshiro.convert(jp, { to: "hiragana" });
    })
}

const onHighlight = () => {
  console.log("Entered helper")

  if (window.getSelection().toString() !== '') {
    toFuri(window.getSelection().toString())
      .then((result) => {
        console.log(result);
      })
  }
}


const Subtitle = ({engSub, jpSub}) => (
  <div className="subtitle" onClick={onHighlight}>
    {jpSub}<br/>{engSub}
  </div>
)

export default Subtitle;
