import React, {Component} from 'react';
import sub_one from '../assets/subtitle_json/Attack.on.Titan.01.To.You.in.2000.Years.720p.Bluray.x264.Kirion.json';

let i=0;

class Subtitle extends Component {
  state={
    subtitle_text: ''
  }

  render() {
    var a=[]
    while (true) {
        if (sub_one[i].start >= this.props.played
          && sub_one[i].end <= this.props.played) {
            a.push(sub_one[i].content)
        }
        else {i+=1;}
      }
    return (
      <div className="subtitle">
        {a}
      </div>
    )
  }
}

export default Subtitle;
