import React, { useState } from 'react';
import Kuroshiro from 'kuroshiro';
import KuromojiAnalyzer from "kuroshiro-analyzer-kuromoji";
import { Tooltip } from 'reactstrap';
import ReactTooltip from "react-tooltip";

const divRef = useRef()

const toFuri = async (jp) => {
  var kuroshiro = new Kuroshiro();
  await kuroshiro.init(new KuromojiAnalyzer({ dictPath: "/dict" }));
  return await kuroshiro.convert(jp, { to: "hiragana" });
}

const onHighlight = () => {
  console.log("Entered onHighlight");
  console.log(window.getSelection().getRangeAt(0).getBoundingClientRect())
  console.log(ReactDOM.findDOMNode(divRef).getRangeAt(0).getBoundingClientRect())


  if (window.getSelection().toString() !== '') {
    ReactTooltip.hide()
    toFuri(window.getSelection().toString()).then(
      (result) => console.log(result)
    )
  }
}

const Subtitle = ({engSub, jpSub}) => {

  const [tooltipOpen, setTooltipOpen] = useState(false);

  const toggle = () => setTooltipOpen(window.getSelection().toString() !== ''&&!tooltipOpen);



  return (
    <div ref={divRef} className="subtitle" onClick={onHighlight}>

      <p><span href="#" id="DisabledAutoHideExample">Sometimes you need to allow users to select text within a tooltip.</span></p>
      <Tooltip placement='top' offset={400} isOpen={tooltipOpen} autohide={false} target="DisabledAutoHideExample" toggle={toggle}>
        {window.getSelection().toString()}
      </Tooltip>

      <a data-tip='custom'>( •̀д•́)</a>
      <ReactTooltip />

      {jpSub}

      <br/>

      {engSub}
    </div>
  );
}

export default Subtitle;
