import React, {Component} from 'react';

class GameState extends Component {
  render() {
    return (
      <div className="score-board">
        <div className="score">
          Type the Hiragana reading!<br /><br />
          {this.props.qBank[0].kanji}<br /><br />
          <input className= "quizAnswer" type="text" />
          <button>Submit</button>
          <button>Skip</button>
        </div>
      </div>
    );
  }

}

export default GameState;
