import React from 'react';
import sub_one from '../assets/subtitle_json/Attack.on.Titan.01.To.You.in.2000.Years.720p.Bluray.x264.Kirion.json';

let counter=0

const playedToSub = (played) => {
  while (counter<sub_one.length) {
    console.log("Entered while, counter: ", counter)
    if (sub_one[counter].start < played && played < sub_one[counter].end) {
      console.log("Entered if, counter: ", counter)
      return sub_one[counter].content
    }
    else {
      console.log("Entered else")
      counter+=1
    }
  }
}

const Subtitle = ({played}) => (
  <div className="subtitle">
    {playedToSub(played)}
  </div>
)

export default Subtitle;
