import React, {Component} from 'react';
import ResponsivePlayer from './ResponsivePlayer';
import Subtitle from './Subtitle';
import './Video.css';
import sub_one from '../assets/subtitle_json/Attack.on.Titan.01.To.You.in.2000.Years.720p.Bluray.x264.Kirion.json';


class Video extends Component {
  state={
    url: 'https://www.youtube.com/watch?v=x7hjx8OPiA0', //Placeholder
    played: 0, //In seconds
    counter: 0,
    engSub: '',
    sub_start: sub_one[0].start,
    sub_end: sub_one[0].end
  }

  playedToSub = (played) => {
    if (this.state.sub_start===played) {
      console.log("Entered if")
      this.setState({
        engSub: sub_one[this.state.counter]
      });
    }

    else if (played===this.state.sub_end) {
      console.log("Entered else if")
      this.setState({
        sub_start: sub_one[this.state.counter+1].start,
        sub_end: sub_one[this.state.counter+1].end,
        counter: this.state.counter+1,
        engSub: ''
      })
    }

  }

  handleProgress = (state) => {
    this.setState({played: state.playedSeconds});
    if (this.state.played > 0) {
      this.playedToSub(this.state.played)
    }
  }

  render() {
    return(
      <div className="container">
        <div className="video-title">Video Learning</div>
        <div className="video-board">
          <ResponsivePlayer
            url={this.state.url}
            handleProgress={this.handleProgress}
          />
          <Subtitle
            engSub={this.state.engSub}
          />
        </div>
      </div>
    );
  }
}

export default Video;
